import React, { useState } from "react";
import axios from "axios";

export default function Register() {
  const [values, setValues] = useState({
    name: "",
    lname: "",
    email: "",
    password: "",
    conPass: "",
  });

  const { name, lname, email, password, conPass } = values;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios
        .post(`/register`, { name, password })
        .then((res) => {
          if (res) {
            console.log("object :>> ", res.data);
            setUserName(res.data);
            setIsAdmin(res.data.isAdmin);
            // localStorage.setItem("isAdmin", JSON.stringify(res.data.isAdmin));
            // localStorage.setItem("token", JSON.stringify(token));
            // navigate('/admin')
          } else {
            console.log("response is nt found");
          }
        });
    } catch (err) {
      console.log("err kiran:>> ", err);
    }
  };

  return (
    <>
      <div>
        <form action="" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="fiName">First Name</label>
            <input
              type="text"
              name="name"
              value={name}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="LName">Last Name</label>
            <input
              type="text"
              name="lname"
              value={lname}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="email">email</label>
            <input
              type="email"
              name="email"
              value={email}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="Password">Password</label>
            <input
              type="password"
              name="password"
              value={password}
              onChange={handleChange}
            />
          </div>
          <div>
            <label htmlFor="Confrim">Confrim Password</label>
            <input
              type="password"
              name="conPass"
              value={conPass}
              onChange={handleChange}
            />
          </div>
          <button type="submit">Register</button>
        </form>
      </div>
    </>
  );
}
