import React, { useState } from "react";
import axios from 'axios'

export default function Register() {
  const [values, setValues] = useState({
    name: "",
    lname: "",
    email: "",
    password: "",
    conPass: "",
  });

  const { name,lname, email, password, conPass } = values

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };


  const handleSubmit = () => {
    const res=axios.post(`/register`,{values}).then((res)=>{
        console.log('res :>> ', res);

    })
    // console.log('object :>> ', values);


  };

  return (
    <>
      <div>
        <form action="" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="fiName">First Name</label>
            <input type="text" name="name" value={name} onChange={handleChange} />
          </div>
          <div>
            <label htmlFor="LName">Last Name</label>
            <input type="text" name="lname" value={lname} onChange={handleChange} />
          </div>
          <div>
            <label htmlFor="email">email</label>
            <input type="email" name="email" value={email} onChange={handleChange} />
          </div>
          <div>
            <label htmlFor="Password">Password</label>
            <input type="password" name="password"  value={password} onChange={handleChange}/>
          </div>
          <div>
            <label htmlFor="Confrim">Confrim Password</label>
            <input type="password" name="conPass"  value={conPass} onChange={handleChange}/>
          </div>
          <button type="submit">Register</button>
        </form>
      </div>
    </>
  );
}
